const githubData = $input.first().json;
const currentContent = Buffer.from(githubData.content, 'base64').toString('utf8');
let recipes = [];
try { recipes = JSON.parse(currentContent); if (!Array.isArray(recipes)) recipes = []; } catch(e) { recipes = []; }

const existingSlugs = recipes.map(r => r.slug).filter(Boolean);
const existingTitles = recipes.map(r => r.title).filter(Boolean);

return [{ json: { existingSlugs, existingTitles, totalExisting: recipes.length } }];

const raw = $input.first().json.choices?.[0]?.message?.content || '';
const cleaned = raw.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

let recipe;
try {
  recipe = JSON.parse(cleaned);
} catch(e) {
  throw new Error('JSON inv√°lido de Grok: ' + e.message + ' | Inicio: ' + cleaned.slice(0, 200));
}

if (!recipe.title || !recipe.slug) {
  throw new Error('Faltan campos requeridos title/slug: ' + JSON.stringify(recipe).slice(0, 200));
}

if ((recipe.description || '').length < 80) {
  throw new Error('Description demasiado corta (' + (recipe.description || '').length + ' chars): ' + recipe.description);
}

// Normalizar slug
recipe.slug = recipe.slug
  .toLowerCase().trim()
  .replace(/[√°√†√§√¢]/g, 'a').replace(/[√©√®√´√™]/g, 'e')
  .replace(/[√≠√¨√Ø√Æ]/g, 'i').replace(/[√≥√≤√∂√¥]/g, 'o')
  .replace(/[√∫√π√º√ª]/g, 'u').replace(/√±/g, 'n')
  .replace(/[^a-z0-9-]/g, '-').replace(/-+/g, '-')
  .replace(/^-|-$/g, '');

// Garantizar campos v3
if (!recipe.origin_cities) recipe.origin_cities = [];
if (!recipe.youtube_query) recipe.youtube_query = recipe.title + ' receta ecuatoriana';
if (!recipe.image_keywords) recipe.image_keywords = [recipe.title, 'ecuadorian food', 'traditional dish'];
if (!recipe.image_alt) recipe.image_alt = recipe.title + ', plato t√≠pico ecuatoriano';

return [{ json: recipe }];

const ytValidated = $input.first().json;
const items = ytValidated.items || [];

// Filtrar solo embeddable
const embeddable = items.filter(item => item.status && item.status.embeddable !== false);

const youtube_videos = embeddable.slice(0, 3).map(item => ({
  title: item.snippet?.title || '',
  videoId: item.id,
  channel: item.snippet?.channelTitle || '',
  url: 'https://www.youtube.com/watch?v=' + item.id,
  embed: 'https://www.youtube-nocookie.com/embed/' + item.id,
  uploadDate: (item.snippet?.publishedAt || '').split('T')[0] || undefined,
  description: (item.snippet?.description || '').slice(0, 200)
}));

return [{ json: { youtube_videos } }];

// Combinar receta de Grok con videos YouTube
const recipe = $('Parsear Receta JSON').first().json;
const youtubeData = $('Procesar Videos YouTube').first().json;

const enriched = Object.assign({}, recipe);

// YouTube videos
if (youtubeData.youtube_videos && youtubeData.youtube_videos.length > 0) {
  enriched.youtube_videos = youtubeData.youtube_videos;
}

// La imagen ya viene de Grok (URL real de Unsplash elegida por el modelo)
// Si no tiene URL valida, usar fallback
if (!enriched.image_url || enriched.image_url.includes('XXXX') || enriched.image_url.length < 30) {
  enriched.image_url = 'https://images.unsplash.com/photo-1567337710282-00832b415979?w=800&q=80';
}

// Limpiar campos auxiliares
delete enriched.youtube_query;
delete enriched.image_keywords;

return [{ json: enriched }];

const recipe = $input.first().json;
const errors = [];

// Campos obligatorios
if (!recipe.slug || recipe.slug.length < 3) errors.push('slug inv√°lido: ' + recipe.slug);
if (!recipe.title || recipe.title.length < 5) errors.push('title muy corto: ' + recipe.title);
if (!recipe.description || recipe.description.length < 80) errors.push('description < 80 chars: ' + (recipe.description || '').length + ' chars');

// Contenido m√≠nimo
if (!recipe.ingredients || recipe.ingredients.length < 8) errors.push('ingredientes < 8: ' + (recipe.ingredients || []).length);
if (!recipe.instructions || recipe.instructions.length < 6) errors.push('instrucciones < 6: ' + (recipe.instructions || []).length);

// Videos (aviso, no bloquea)
const hasVideos = recipe.youtube_videos && recipe.youtube_videos.length > 0;
if (!hasVideos) {
  console.warn('Receta sin videos YouTube ‚Äî se publica igual');
}

if (errors.length > 0) {
  throw new Error('QUALITY_GATE_FAILED: ' + errors.join(' | '));
}

console.log('Quality Gate OK:', recipe.slug, '| videos:', (recipe.youtube_videos || []).length);
return [{ json: recipe }];

const newRecipe = $('Quality Gate').first().json;
const githubData = $input.first().json;
const fileSha = githubData.sha;

const currentContent = Buffer.from(githubData.content, 'base64').toString('utf8');
let recipes = [];
try {
  recipes = JSON.parse(currentContent);
  if (!Array.isArray(recipes)) recipes = [];
} catch(e) { recipes = []; }

// Dedup por slug
if (recipes.some(r => r.slug === newRecipe.slug)) {
  throw new Error('DUPLICADO_SLUG: ' + newRecipe.slug);
}

// Auto-increment ID
const maxId = recipes.reduce((max, r) => Math.max(max, Number(r.id) || 0), 0);
newRecipe.id = maxId + 1;
newRecipe.created_at = new Date().toISOString();

// Prepend (m√°s reciente primero)
recipes.unshift(newRecipe);

const newContent = Buffer.from(JSON.stringify(recipes, null, 2), 'utf8').toString('base64');
const hasPlaces = (newRecipe.places || []).length;
const hasVideos = (newRecipe.youtube_videos || []).length;
const extras = [hasPlaces ? hasPlaces + ' lugares' : '', hasVideos ? hasVideos + ' videos' : ''].filter(Boolean).join(', ');

return [{ json: {
  sha: fileSha,
  content: newContent,
  slug: newRecipe.slug,
  title: newRecipe.title,
  id: newRecipe.id,
  total: recipes.length,
  extras: extras || 'sin enriquecimiento'
} }];
